fieldValues.fieldValue:
={{ 
  (() => {
    const existingLeads = ($json.lead_data && Array.isArray($json.lead_data)) ? $json.lead_data : [];
    const newLeads = $('Set Columns').all().map(item => ({
      "email": item.json.Email,
      "first_name": item.json['First Name'],
      "last_name": item.json['Last Name'],
      "linkedin_url": item.json['LinkedIn URL'],
      "company_linkedin_url": item.json['Company LinkedIn URL'],
      "company": item.json.Company,
      "company_url": item.json['Company URL'],
      "location": item.json.Location,
      "company_headcount": item.json['Company Headcount'],
      "company_industry": item.json['Company Industry'],
      "phone_number": item.json['Phone Number'],
      "personalized_message": item.json['Personalized Sentence'],
      "enrichment_status": "enriched"
    }));

    // Get existing emails to avoid duplicates
    const existingEmails = new Set(existingLeads.map(lead => lead.email));

    // Filter out leads that already exist AND skip leads with empty personalized messages
    const uniqueNewLeads = newLeads.filter(lead => 
      !existingEmails.has(lead.email) && 
      lead.personalized_message && 
      lead.personalized_message.trim() !== ''
    );

    // Return combined array
    return [...existingLeads, ...uniqueNewLeads];
  })()
}}

